package apd

import "math/big"

var (
	bigZero           = big.NewInt(0)
	bigOne            = big.NewInt(1)
	bigTwo            = big.NewInt(2)
	bigTen            = big.NewInt(10)
	decimalZero       = New(0, 0)
	decimalHalf       = New(5, -1)
	decimalZeroPtNine = New(9, -1)
	decimalOne        = New(1, 0)
	decimalOnePtOne   = New(11, -1)
	decimalTwo        = New(2, 0)

	decimalLog10 *Decimal
	decimalE     *Decimal
)

func init() {
	var err error
	var c Context
	if decimalLog10, err = c.NewFromString(strLog10); err != nil {
		panic(err)
	}
	if decimalE, err = c.NewFromString(strE); err != nil {
		panic(err)
	}
}

const strLog10 = "2."

const strE = "2."
