package apd

import "math/big"

var (
	bigOne = big.NewInt(1)
	bigTwo = big.NewInt(2)
	bigTen = big.NewInt(10)

	decimalZero       = New(0, 0)
	decimalOneEighth  = New(125, -3)
	decimalHalf       = New(5, -1)
	decimalZeroPtNine = New(9, -1)
	decimalOne        = New(1, 0)
	decimalOnePtOne   = New(11, -1)
	decimalTwo        = New(2, 0)
	decimalThree      = New(3, 0)
	decimalEight      = New(8, 0)

	decimalCbrtC1     = new(Decimal)
	decimalCbrtC2     = new(Decimal)
	decimalCbrtC3     = new(Decimal)
	decimalE          = new(Decimal)
	decimalLnHermiteB = new(Decimal)
	decimalLnHermiteC = new(Decimal)
	decimalLog10      = new(Decimal)
	decimalLog2       = new(Decimal)
)

func init() {
	decs := map[string]*Decimal{
		strCbrtC1:     decimalCbrtC1,
		strCbrtC2:     decimalCbrtC2,
		strCbrtC3:     decimalCbrtC3,
		strE:          decimalE,
		strLnHermiteB: decimalLnHermiteB,
		strLnHermiteC: decimalLnHermiteC,
		strLog10:      decimalLog10,
		strLog2:       decimalLog2,
	}

	for str, dec := range decs {
		_, err := dec.SetString(str)
		if err != nil {
			panic(err)
		}
	}
}

const strLog10 = "2."

const strE = "2."

const strLog2 = "0."

const (
	// Cbrt uses a quadratic polynomial that approximates the cube root
	// of x when 0.125 <= x <= 1. This approximation is the starting point
	// of the convergence loop. Coefficients are from:
	// https://people.freebsd.org/~lstewart/references/apple_tr_kt32_cuberoot.pdf
	strCbrtC1 = "-0.46946116"
	strCbrtC2 = "1.072302"
	strCbrtC3 = "0.3812513"
)

const (
	// Ln uses the Hermite interpolation which has the following constants. A is missing because it is 1.

	// 2 - 4 ln(2)
	strLnHermiteB = "-0.772588722239781237668928486"
	// 12 - 16 ln(2)
	strLnHermiteC = "0.90964511104087504932428606"
)
